import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
import { gameRooms } from '../iAPI.interface';

@Component({
  selector: 'app-lobby',
  templateUrl: './lobby.component.html',
  styleUrls: ['./lobby.component.scss'],
})
export class LobbyComponent implements OnInit {
  inputValue: string = '';
  gameRooms: gameRooms = {};
  userName: string = '';
  errorMessage: string = ''; // Add error message variable
  showLobby: Boolean = true;
  roomKey = '';
  constructor(private apiService: ApiService) {}

  getRooms() {
    this.apiService.getRooms().subscribe(
      (data) => {
        this.gameRooms = data;
      },
      (error) => {
        this.errorMessage = 'Error fetching rooms: ' + error.message;
      }
    );
  }

  createRoom() {
    this.apiService.createRoom(this.inputValue).subscribe(
      (response) => {
        console.log('POST Request Successful', response);
        this.getRooms();
      },
      (error) => {
        console.error('POST Request Error', error);
      }
    );
  }

  createUser() {}

  enterRoom(roomID: string) {
    this.showLobby = false;
    this.roomKey = roomID;
  }

  closeRoom(): void {
    this.showLobby = true;
  }

  ngOnInit(): void {
    this.getRooms();
  }

  deleteRoom(roomID: string) {
    this.apiService.deleteRoom(roomID).subscribe((Response) => {
      console.log('Delete Request Successful', Response);
      this.getRooms();
    });
  }
}
