import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { ApiService } from '../api.service';
import { gameRooms } from '../iAPI.interface';

@Component({
  selector: 'app-lobby',
  templateUrl: './lobby.component.html',
  styleUrls: ['./lobby.component.scss'],
})
export class LobbyComponent implements OnInit {
  inputValue: string = '';
  gameRooms: gameRooms = {};
  errorMessage: string = ''; // Add error message variable
  showLobby = true;
  @Output() enterRoomID = new EventEmitter<number>();

  constructor(private apiService: ApiService) {}

  getRooms() {
    this.apiService.getRooms().subscribe(
      (data) => {
        this.gameRooms = data;
      },
      (error) => {
        this.errorMessage = 'Error fetching rooms: ' + error.message;
      }
    );
  }

  createRoom() {
    this.apiService.createRoom(this.inputValue).subscribe(
      (response) => {
        console.log('POST Request Successful', response);
        this.getRooms();
      },
      (error) => {
        console.error('POST Request Error', error);
      }
    );
  }

  enterRoom(roomID: string) {
    this.showLobby = false;
    this.enterRoomID.emit(Number(roomID));
  }

  ngOnInit(): void {
    this.getRooms();
  }
}
<div class="d-flex justify-content-center">
  <div class="w-25 card border border-1 border-secondary rounded m-2">
    <div class="card-title">
      <div class="text-start m-2">
        <h3>Create a room</h3>
      </div>
      <div class="card-body d-flex justify-content-center">
        <input
          class="mx-2"
          type="text"
          placeholder="Room Name"
          [(ngModel)]="inputValue"
        />
        <button class="btn btn-primary mt-2" (click)="createRoom()">
          Create room
        </button>
      </div>
    </div>
  </div>
</div>
<div class="d-flex justify-content-center">
  <div class="w-25 card border border-1 border-secondary rounded m-2">
    <div class="card-title">
      <div class="text-start m-2">
        <h3>Enter a room</h3>
      </div>
    </div>
  </div>
</div>
<div *ngIf="showLobby">
  <div
    class="d-flex justify-content-center"
    *ngFor="let room of gameRooms | keyvalue"
  >
    <div class="w-25 card border border-1 border-secondary rounded m-2">
      <div class="card-title text-start m-2">
        <h3>{{ room.value.room_name }}</h3>
      </div>
      <div class="card-body d-flex justify-content-center">
        <button class="btn btn-primary mt-2" (click)="enterRoom(room.key)">
          Enter room
        </button>
      </div>
    </div>
  </div>
</div>
